import { defineStore } from "pinia";
export const useContentStore = defineStore({
  id: "content",
  state: () => {
    //定义state的值
    return {
      title: "",
    };
  },

  actions: {
    updateTitle(title) {
      //更新title的值
      this.title = title;
    },

    addSum() {
      this.sum++;
    },
  },
});
